int ObTableScanStoreRowIterator::rescan(const ObRangeArray& key_ranges, const ObPosArray& range_array_pos)
{
  int ret = OB_SUCCESS;
  if (OB_UNLIKELY(!is_inited_)) {
    ret = OB_NOT_INIT;
    STORAGE_LOG(WARN, "The ObTableScanStoreRowIterator has not been inited, ", K(ret));
  } else if (&scan_param_->key_ranges_ != &key_ranges || &scan_param_->range_array_pos_ != &range_array_pos) {
    ret = OB_ERR_UNEXPECTED;
    STORAGE_LOG(WARN, "scan_param is not the same", K(ret), K(scan_param_), K(key_ranges), K(range_array_pos));
  } else {
    STORAGE_LOG(DEBUG, "table scan iterate rescan", K_(is_inited), K(scan_param_));
    // there's no need to reset main_table_param_ and index_table_param_
    // scan_param only reset query range fields in ObTableScan::rt_rescan()
    range_iter_.reuse();
    main_iter_ = NULL;
    reuse_row_iters();
    main_table_ctx_.reuse();
    index_table_ctx_.reuse();
    is_iter_opened_ = false;

    if (OB_FAIL(range_iter_.set_scan_param(*scan_param_))) {
      STORAGE_LOG(WARN, "set scan param to range iterator failed", K(ret));
    } else if (OB_FAIL(prepare_table_context(row_filter_))) {
      STORAGE_LOG(WARN, "fail to prepare table context", K(ret));
    } else {
      if (OB_FAIL(open_iter())) {
        STORAGE_LOG(WARN, "fail to open iter", K(ret));
      } else {
        is_iter_opened_ = true;
      }
    }
  }
  return ret;
}

int ObTableScanStoreRowIterator::open_iter()
{
  int ret = OB_SUCCESS;
  ObBatch batch;
  void* buf = NULL;
  if (OB_FAIL(range_iter_.get_next(batch))) {
    if (OB_ITER_END != ret) {
      STORAGE_LOG(WARN, "fail to get batch range", K(ret));
    } else {
      ret = OB_SUCCESS;
    }
  } else {
    get_table_param_.partition_store_ = partition_store_;
    get_table_param_.frozen_version_ = scan_param_->frozen_version_;
    get_table_param_.sample_info_ = scan_param_->sample_info_;
    switch (batch.type_) {
      case ObBatch::T_GET: {
        if (NULL == single_merge_) {
          if (NULL == (buf = scan_param_->allocator_->alloc(sizeof(ObSingleMerge)))) {
            ret = OB_ALLOCATE_MEMORY_FAILED;
            STORAGE_LOG(WARN, "Fail to allocate memory, ", K(ret));
          } else {
            single_merge_ = new (buf) ObSingleMerge();
            if (OB_FAIL(init_scan_iter(scan_param_->scan_flag_.is_index_back(), *single_merge_))) {
              STORAGE_LOG(WARN, "Fail to init single merge, ", K(ret));
            } else {
              use_fuse_row_cache_ = !single_merge_->is_read_memtable_only();
            }
          }
        }
        if (OB_SUCC(ret)) {
          main_table_ctx_.use_fuse_row_cache_ = use_fuse_row_cache_;
          if (OB_FAIL(single_merge_->open(*batch.rowkey_))) {
            STORAGE_LOG(WARN, "Fail to open multiple get merge iterator, ", K(ret));
          } else {
            main_iter_ = single_merge_;
          }
        }
        break;
      }
      case ObBatch::T_MULTI_GET: {
        if (NULL == get_merge_) {
          if (NULL == (buf = scan_param_->allocator_->alloc(sizeof(ObMultipleGetMerge)))) {
            ret = OB_ALLOCATE_MEMORY_FAILED;
            STORAGE_LOG(WARN, "Fail to allocate memory, ", K(ret));
          } else {
            get_merge_ = new (buf) ObMultipleGetMerge();
            if (OB_FAIL(init_scan_iter(scan_param_->scan_flag_.is_index_back(), *get_merge_))) {
              STORAGE_LOG(WARN, "Fail to init get merge, ", K(ret));
            } else {
              use_fuse_row_cache_ = !get_merge_->is_read_memtable_only();
            }
          }
        }
        if (OB_SUCC(ret)) {
          main_table_ctx_.use_fuse_row_cache_ = use_fuse_row_cache_;
          if (OB_FAIL(get_merge_->open(*batch.rowkeys_))) {
            STORAGE_LOG(WARN, "Fail to open multiple get merge iterator, ", K(ret));
          } else {
            main_iter_ = get_merge_;
          }
        }
        break;
      }
      case ObBatch::T_SCAN: {
        if (NULL == scan_merge_) {
          if (OB_ISNULL(buf = scan_param_->allocator_->alloc(sizeof(ObMultipleScanMerge)))) {
            ret = OB_ALLOCATE_MEMORY_FAILED;
            STORAGE_LOG(WARN, "Fail to allocate memory, ", K(ret));
          } else {
            scan_merge_ = new (buf) ObMultipleScanMerge();
            if (OB_FAIL(init_scan_iter(scan_param_->scan_flag_.is_index_back(), *scan_merge_))) {
              STORAGE_LOG(WARN, "Fail to init scan merge, ", K(ret));
            }
          }
        }

        if (OB_SUCC(ret)) {
          if (scan_param_->sample_info_.is_block_sample()) {
            if (NULL == block_sample_iterator_) {
              if (OB_ISNULL(buf = scan_param_->allocator_->alloc(sizeof(ObBlockSampleIterator)))) {
                ret = OB_ALLOCATE_MEMORY_FAILED;
                STORAGE_LOG(WARN, "failed to allocate block_sample_iterator_", K(ret));
              } else {
                block_sample_iterator_ = new (buf) ObBlockSampleIterator(scan_param_->sample_info_);
              }
            }
            if (OB_SUCC(ret)) {
              if (OB_FAIL(block_sample_iterator_->open(*scan_merge_,
                      scan_param_->scan_flag_.is_index_back() ? index_table_ctx_ : main_table_ctx_,
                      *batch.range_,
                      get_table_param_,
                      scan_param_->scan_flag_.is_reverse_scan()))) {
                STORAGE_LOG(WARN, "failed to open block_sample_iterator_", K(ret));
              } else {
                main_iter_ = block_sample_iterator_;
              }
            }
          } else {
            if (OB_FAIL(scan_merge_->open(*batch.range_))) {
              STORAGE_LOG(WARN, "Fail to open multiple scan merge iterator, ", K(ret), K(*scan_param_));
            } else {
              main_iter_ = scan_merge_;
            }
          }
        }
        break;
      }
      case ObBatch::T_MULTI_SCAN: {
        if (NULL == multi_scan_merge_) {
          if (NULL == (buf = scan_param_->allocator_->alloc(sizeof(ObMultipleMultiScanMerge)))) {
            ret = OB_ALLOCATE_MEMORY_FAILED;
            STORAGE_LOG(WARN, "Fail to allocate memory, ", K(ret));
          } else {
            multi_scan_merge_ = new (buf) ObMultipleMultiScanMerge();
            if (OB_FAIL(init_scan_iter(scan_param_->scan_flag_.is_index_back(), *multi_scan_merge_))) {
              STORAGE_LOG(WARN, "fail to init multi scan merge", K(ret));
            }
          }
        }
        if (OB_SUCC(ret)) {
          if (OB_FAIL(multi_scan_merge_->open(*batch.ranges_))) {
            STORAGE_LOG(WARN, "Fail to open multiple multi_scan merge iterator, ", K(ret));
          } else {
            main_iter_ = multi_scan_merge_;
          }
        }
        break;
      }
      default: {
        ret = OB_INVALID_ARGUMENT;
        STORAGE_LOG(WARN, "invalid batch type", K(ret), K(batch.type_));
      }
    }

    if (OB_SUCC(ret)) {
      if (scan_param_->sample_info_.is_row_sample()) {
        if (NULL == row_sample_iterator_) {
          if (OB_ISNULL(buf = scan_param_->allocator_->alloc(sizeof(ObRowSampleIterator)))) {
            ret = OB_ALLOCATE_MEMORY_FAILED;
            STORAGE_LOG(WARN, "failed to allocate memory", K(ret));
          } else {
            row_sample_iterator_ = new (buf) ObRowSampleIterator(scan_param_->sample_info_);
          }
        }
        if (OB_SUCC(ret)) {
          if (OB_FAIL(row_sample_iterator_->open(*main_iter_))) {
            STORAGE_LOG(WARN, "failed to open row_sample_iterator", K(ret));
          } else {
            main_iter_ = row_sample_iterator_;
          }
        }
      }
    }

    if (OB_SUCC(ret)) {
      if (scan_param_->scan_flag_.is_index_back()) {
        if (NULL == index_merge_) {
          if (NULL == (buf = scan_param_->allocator_->alloc(sizeof(ObIndexMerge)))) {
            ret = OB_ALLOCATE_MEMORY_FAILED;
            STORAGE_LOG(WARN, "Fail to allocate memory, ", K(ret));
          } else {
            index_merge_ = new (buf) ObIndexMerge();
            if (OB_FAIL(index_merge_->init(main_table_param_, index_table_param_, main_table_ctx_, get_table_param_))) {
              STORAGE_LOG(WARN, "Fail to init index merge, ", K(ret));
            }
          }
        }

        if (OB_SUCC(ret)) {
          main_table_ctx_.use_fuse_row_cache_ = use_fuse_row_cache_;
          if (OB_FAIL(index_merge_->open(*main_iter_))) {
            STORAGE_LOG(WARN, "Fail to open index merge iterator, ", K(ret));
          } else {
            main_iter_ = index_merge_;
          }
        }
      }
    }
  }
  return ret;
}

int ObMultipleScanMerge::open(const ObExtStoreRange& range)
{
  int ret = OB_SUCCESS;

  if (OB_UNLIKELY(!range.get_range().is_valid())) {
    STORAGE_LOG(WARN, "Invalid range, ", K(range), K(ret));
  } else if (OB_FAIL(ObMultipleMerge::open())) {
    STORAGE_LOG(WARN, "Fail to open ObMultipleMerge, ", K(ret));
  } else if (OB_FAIL(const_cast<ObExtStoreRange&>(range).to_collation_free_range_on_demand_and_cutoff_range(
                 *access_ctx_->allocator_))) {
    STORAGE_LOG(WARN, "fail to get collation free rowkey", K(ret));
  } else {
    row_filter_ = NULL;

    if (NULL != access_ctx_->row_filter_ && tables_handle_.has_split_source_table(access_ctx_->pkey_)) {
      row_filter_ = access_ctx_->row_filter_;
    }

    range_ = &range;
    if (OB_FAIL(ObMultipleScanMergeImpl::prepare_loser_tree())) {
      STORAGE_LOG(WARN, "fail to prepare loser tree", K(ret));
    } else if (OB_FAIL(construct_iters())) {
      STORAGE_LOG(WARN, "fail to construct iters", K(ret));
    } else if (OB_UNLIKELY(access_ctx_->need_prewarm())) {
      access_ctx_->store_ctx_->warm_up_ctx_->record_scan(*access_param_, *access_ctx_, *range_);
    }
  }

  return ret;
}

int ObOperator::get_next_row()
{
  int ret = OB_SUCCESS;
  if (OB_UNLIKELY(!startup_passed_)) {
    bool filtered = false;
    if (OB_FAIL(startup_filter(filtered))) {
      LOG_WARN("do startup filter failed", K(ret), "op", op_name());
    } else {
      if (filtered) {
        ret = OB_ITER_END;
      } else {
        startup_passed_ = true;
      }
    }
  }

  while (OB_SUCC(ret)) {
    if (OB_FAIL(inner_get_next_row())) {
      if (OB_ITER_END != ret) {
        LOG_WARN("inner get next row failed", K(ret), "type", spec_.type_, "op", op_name());
      }
    } else {
      if (!spec_.filters_.empty()) {
        bool filtered = false;
        if (OB_FAIL(filter_row(filtered))) {
          LOG_WARN("filter row failed", K(ret), "type", spec_.type_, "op", op_name());
        } else {
          if (filtered) {
            continue;
          }
        }
      }
    }
    break;
  }

  if (OB_SUCCESS == ret) {
    op_monitor_info_.output_row_count_++;
    if (!got_first_row_) {
      op_monitor_info_.first_row_time_ = oceanbase::common::ObClockGenerator::getClock();
      ;
      got_first_row_ = true;
    }
  } else if (OB_ITER_END == ret) {
    int tmp_ret = drain_exch();
    if (OB_SUCCESS != tmp_ret) {
      LOG_WARN("drain exchange data failed", K(tmp_ret));
    }
    if (got_first_row_) {
      op_monitor_info_.last_row_time_ = oceanbase::common::ObClockGenerator::getClock();
    }
  }
  return ret;
}

int ObNestedLoopJoinOp::inner_get_next_row()
{
  int ret = OB_SUCCESS;
  if (OB_UNLIKELY(LEFT_SEMI_JOIN == MY_SPEC.join_type_ || LEFT_ANTI_JOIN == MY_SPEC.join_type_)) {
    if (OB_FAIL(join_row_with_semi_join())) {
      if (ret != OB_ITER_END) {
        LOG_WARN("failed to join row with semi join", K(ret));
      }
    }
  } else {
    state_operation_func_type state_operation = NULL;
    state_function_func_type state_function = NULL;
    int func = -1;
    output_row_produced_ = false;
    while (OB_SUCC(ret) && !output_row_produced_) {
      state_operation = this->ObNestedLoopJoinOp::state_operation_func_[state_];
      if (OB_ITER_END == (ret = (this->*state_operation)())) {
        func = FT_ITER_END;
        ret = OB_SUCCESS;
      } else if (OB_FAIL(ret)) {
        LOG_WARN("failed state operation", K(ret), K(state_));
      } else {
        func = FT_ITER_GOING;
      }
      if (OB_SUCC(ret)) {
        state_function = this->ObNestedLoopJoinOp::state_function_func_[state_][func];
        if (OB_FAIL((this->*state_function)()) && OB_ITER_END != ret) {
          LOG_WARN("failed state function", K(ret), K(state_), K(func));
        }
      }
    }  // while end
  }
  if (OB_ITER_END == ret) {
    if (OB_FAIL(set_param_null())) {
      LOG_WARN("failed to set param null", K(ret));
    } else {
      ret = OB_ITER_END;
    }
  }

  return ret;
}

int ObNestedLoopJoinOp::read_left_func_going()
{
  int ret = OB_SUCCESS;
  if (MY_SPEC.use_group_) {
    // do nothing
  } else if (!MY_SPEC.rescan_params_.empty()) {
    ObExecContext::ObPlanRestartGuard restart_plan(ctx_);
    if (OB_FAIL(prepare_rescan_params())) {
      LOG_WARN("failed to prepare rescan params", K(ret));
    } else if (OB_FAIL(right_->rescan())) {
      LOG_WARN("failed to rescan right op", K(ret));
    } else { /*do nothing*/
    }
  } else {
    if (OB_FAIL(prepare_rescan_params())) {
      LOG_WARN("failed to prepare rescan params", K(ret));
    } else if (OB_FAIL(right_->rescan())) {
      LOG_WARN("failed to rescan right op", K(ret));
    } else { /*do nothing*/
    }
  }
  state_ = JS_READ_RIGHT;

  return ret;
}

int ObTableScanOp::rescan()
{
  int ret = OB_SUCCESS;
  if (ctx_.is_gi_restart()) {
    // this scan is started by a gi operator, so, scan a new range.
    if (OB_FAIL(get_gi_task_and_restart())) {
      LOG_WARN("fail to get gi task and scan", K(ret));
    }
  } else if (MY_SPEC.batch_scan_flag_) {
    // do nothing
  } else if (OB_INVALID_INDEX == MY_INPUT.get_location_idx()) {
    iter_end_ = true;
    if (OB_UNLIKELY(NULL != result_)) {
      ret = OB_ERR_UNEXPECTED;
      LOG_ERROR("result_ should be NULL", K(ret));
    }
  } else if (is_virtual_table(MY_SPEC.ref_table_id_) || MY_SPEC.for_update_) {
    ret = vt_rescan();
  } else if (!range_array_pos.empty()) {
    ret = OB_NOT_SUPPORTED;
    LOG_USER_ERROR(OB_NOT_SUPPORTED, "Batch iterator rescan");
  } else {
    ret = rt_rescan();
  }
  return ret;
}

int ObTableScanOp::rt_rescan()
{
  int ret = OB_SUCCESS;
  if (OB_FAIL(reset_query_range())) {
    LOG_WARN("failed to reset query range", K(ret));
  } else if (OB_FAIL(add_query_range())) {
    LOG_WARN("failed to add query range", K(ret));
  } else if (OB_FAIL(rescan_after_adding_query_range())) {
    LOG_WARN("failed to rescan", K(ret));
  } else {
  }
  return ret;
}

int ObTableScanOp::rescan_after_adding_query_range()
{
  int ret = OB_SUCCESS;
  if (OB_FAIL(ObOperator::rescan())) {
    LOG_WARN("rescan operator failed", K(ret));
  } else if (OB_ISNULL(result_)) {
    ret = OB_ERR_UNEXPECTED;
    LOG_ERROR("result_ is NULL", K(ret));
  } else if (MY_SPEC.is_vt_mapping_ && OB_FAIL(vt_result_converter_->convert_key_ranges(scan_param_.key_ranges_))) {
    LOG_WARN("failed to convert key ranges", K(ret));
  } else if (OB_FAIL(static_cast<ObTableScanIterator*>(result_)->rescan(scan_param_))) {
    LOG_WARN("failed to rescan", K(ret), K(scan_param_));
  } else {
    iter_end_ = false;
  }
  return ret;
}

int ObTableScanIterIterator::rescan(ObTableScanParam& scan_param)
{
  int ret = OB_SUCCESS;
  range_row_iter_.reset();
  if (OB_FAIL(store_row_iter_.rescan(scan_param.key_ranges_, scan_param.range_array_pos_))) {
    STORAGE_LOG(WARN, "fail to rescan", K(ret));
  } else if (OB_FAIL(range_row_iter_.init(scan_param.scan_flag_.is_reverse_scan(), store_row_iter_))) {
    STORAGE_LOG(WARN, "fail to init range row iterator", K(ret));
  } else {
    const int64_t range_array_cnt = scan_param.range_array_pos_.count();
    is_reverse_scan_ = scan_param.scan_flag_.is_reverse_scan();
    range_array_cnt_ = 0 == range_array_cnt ? 1 : range_array_cnt;  // zero range means whole range
    range_array_cursor_ = is_reverse_scan_ ? range_array_cnt_ : -1;
  }
  return ret;
}

int ObMultipleScanMerge::construct_iters()
{
  int ret = OB_SUCCESS;
  const ObIArray<ObITable*>& tables = tables_handle_.get_tables();

  consumer_.reset();

  if (OB_ISNULL(range_)) {
    ret = OB_ERR_UNEXPECTED;
    STORAGE_LOG(WARN, "range is NULL", K(ret));
  } else if (OB_UNLIKELY(iters_.count() > 0 && iters_.count() != tables.count())) {
    ret = OB_ERR_UNEXPECTED;
    STORAGE_LOG(WARN,
        "iter cnt is not equal to table cnt",
        K(ret),
        "iter cnt",
        iters_.count(),
        "table cnt",
        tables.count(),
        KP(this));
  } else if (tables.count() > 0) {
    ObITable* table = NULL;
    ObStoreRowIterator* iter = NULL;
    const ObTableIterParam* iter_pram = NULL;
    const bool use_cache_iter = iters_.count() > 0;
    const int64_t table_cnt = tables.count() - 1;

    if (OB_FAIL(loser_tree_.init(tables.count(), *access_ctx_->stmt_allocator_))) {
      STORAGE_LOG(WARN, "init loser tree fail", K(ret));
    }

    for (int64_t i = table_cnt; OB_SUCC(ret) && i >= 0; --i) {
      if (OB_FAIL(tables.at(i, table))) {
        STORAGE_LOG(WARN, "Fail to get ith store, ", K(i), K(ret));
      } else if (OB_ISNULL(iter_pram = get_actual_iter_param(table))) {
        ret = OB_ERR_UNEXPECTED;
        STORAGE_LOG(WARN, "Fail to get access param", K(i), K(ret), K(*table));
      } else if (!use_cache_iter) {
        if (OB_FAIL(table->scan(*iter_pram, *access_ctx_, *range_, iter))) {
          STORAGE_LOG(WARN, "Fail to get iterator, ", K(*iter_pram), K(*access_ctx_), K(ret), K(i));
        } else if (OB_FAIL(iters_.push_back(iter))) {
          iter->~ObStoreRowIterator();
          STORAGE_LOG(WARN, "Fail to push iter to iterator array, ", K(ret), K(i));
        }
      } else if (OB_ISNULL(iter = iters_.at(table_cnt - i))) {
        ret = OB_ERR_UNEXPECTED;
        STORAGE_LOG(WARN, "Unexpected null iter", K(ret), "idx", table_cnt - i, K_(iters));
      } else if (OB_FAIL(iter->init(*iter_pram, *access_ctx_, table, range_))) {
        STORAGE_LOG(WARN, "failed to init scan iter", K(ret), "idx", table_cnt - i);
      }

      if (OB_SUCC(ret)) {
        consumer_.add_consumer(i);
        if (iter->is_base_sstable_iter()) {
          consumer_.set_base_iter_idx(table_cnt - i);
        }
        STORAGE_LOG(DEBUG, "[PUSHDOWN]", K_(consumer), K(iter->is_base_sstable_iter()));
        STORAGE_LOG(DEBUG, "add iter for consumer", KPC(table), KPC(access_param_));
      }
    }

    if (OB_SUCC(ret)) {
      if (OB_FAIL(prepare_range_skip())) {
        STORAGE_LOG(WARN, "Fail to prepare range skip", K(ret));
      }
    }
  }
  return ret;
}

int ObSSTable::scan(const ObTableIterParam& param, ObTableAccessContext& context,
    const common::ObExtStoreRange& key_range, ObStoreRowIterator*& row_iter)
{
  int ret = OB_SUCCESS;

  if (OB_UNLIKELY(!is_valid())) {
    ret = OB_ERR_UNEXPECTED;
    STORAGE_LOG(WARN, "sstable is not in not ready for access", K(ret), K_(meta), K_(status));
  } else if (OB_UNLIKELY(param.table_id_ != key_.table_id_)) {
    ret = OB_ERR_SYS;
    STORAGE_LOG(ERROR, "table id not match", K(ret), K(key_.table_id_), K(param.table_id_), K(*this), K(param));
  } else if (OB_UNLIKELY(!param.is_valid() || (!context.is_valid()) || (!key_range.get_range().is_valid()))) {
    ret = OB_INVALID_ARGUMENT;
    STORAGE_LOG(WARN, "Invalid argument, ", K(param), K(context), K(key_range), K(ret));
  } else {
    void* buf = NULL;
    ObISSTableRowIterator* row_scanner = NULL;
    if (context.query_flag_.is_whole_macro_scan()) {
      if (NULL == (buf = context.allocator_->alloc(sizeof(ObSSTableRowWholeScanner)))) {
        ret = OB_ALLOCATE_MEMORY_FAILED;
        STORAGE_LOG(WARN, "Fail to allocate memory, ", K(ret));
      } else {
        row_scanner = new (buf) ObSSTableRowWholeScanner();
      }
    } else if (is_multi_version_minor_sstable()) {
      if (NULL == (buf = context.allocator_->alloc(sizeof(ObSSTableMultiVersionRowScanner)))) {
        ret = OB_ALLOCATE_MEMORY_FAILED;
        STORAGE_LOG(WARN, "Fail to allocate memory, ", K(ret));
      } else {
        row_scanner = new (buf) ObSSTableMultiVersionRowScanner();
      }
    } else {
      if (NULL == (buf = context.allocator_->alloc(sizeof(ObSSTableRowScanner)))) {
        ret = OB_ALLOCATE_MEMORY_FAILED;
        STORAGE_LOG(WARN, "Fail to allocate memory, ", K(ret));
      } else {
        row_scanner = new (buf) ObSSTableRowScanner();
      }
    }

    if (OB_SUCC(ret)) {
      if (OB_FAIL(row_scanner->init(param, context, this, &key_range))) {
        STORAGE_LOG(WARN, "Fail to open row scanner, ", K(ret), K(param), K(context), K(key_range));
      }
    }
    if (OB_FAIL(ret)) {
      if (NULL != row_scanner) {
        row_scanner->~ObISSTableRowIterator();
      }
    } else {
      row_iter = row_scanner;
    }
  }

  return ret;
}

int ObISSTableRowIterator::init(
    const ObTableIterParam& iter_param, ObTableAccessContext& access_ctx, ObITable* table, const void* query_range)
{
  int ret = OB_SUCCESS;

  if (OB_ISNULL(query_range) || OB_ISNULL(table)) {
    ret = OB_INVALID_ARGUMENT;
    STORAGE_LOG(WARN, "Invalid argument to init ObISSTableRowIterator", K(ret), KP(query_range), KP(table));
  } else if (OB_FAIL(inner_open(iter_param, access_ctx, table, query_range))) {
    STORAGE_LOG(WARN, "Failed to inner open ObISSTableRowIterator", K(ret));
  } else {
    sstable_ = static_cast<ObSSTable*>(table);
    if (sstable_->has_lob_macro_blocks()) {
      bool has_lob_column = false;
      if (OB_FAIL(iter_param.has_lob_column_out(access_ctx.use_fuse_row_cache_, has_lob_column))) {
        STORAGE_LOG(WARN, "fail to check has lob column", K(ret));
      } else if (has_lob_column) {
        if (OB_FAIL(add_lob_reader(iter_param, access_ctx, *sstable_))) {
          STORAGE_LOG(WARN, "Failed to add lob reader", K(ret));
        } else if (OB_ISNULL(lob_reader_)) {
          ret = OB_ERR_UNEXPECTED;
          STORAGE_LOG(WARN, "Unexpected null pointer after add lob reader", K(ret));
        }
      }
    }
  }
  return ret;
}

int ObSSTableRowIterator::inner_open(
    const ObTableIterParam& iter_param, ObTableAccessContext& access_ctx, ObITable* table, const void* query_range)
{
  int ret = OB_SUCCESS;

  if (OB_UNLIKELY(is_opened_)) {
    ret = OB_INIT_TWICE;
    STORAGE_LOG(WARN, "The ObSSTableRowIterator has been opened, ", K(ret));
  } else if (OB_UNLIKELY(NULL == query_range || NULL == table)) {
    ret = OB_INVALID_ARGUMENT;
    STORAGE_LOG(WARN, "Invalid argument, ", K(ret), KP(query_range), KP(table));
  } else if (OB_FAIL(get_handle_cnt(query_range, read_handle_cnt_, micro_handle_cnt_))) {
    STORAGE_LOG(WARN, "Fail to get handle cnt, ", K(ret), KP(query_range));
  } else if (OB_UNLIKELY(read_handle_cnt_ <= 0) || OB_UNLIKELY(micro_handle_cnt_ <= 0)) {
    ret = OB_ERR_UNEXPECTED;
    STORAGE_LOG(WARN, "Unexpected error, ", K(ret), K_(read_handle_cnt), K_(micro_handle_cnt));
  } else if (OB_FAIL(init_handle_mgr(iter_param, access_ctx, query_range))) {
    STORAGE_LOG(WARN, "fail to init handle mgr", K(ret), K(iter_param), K(access_ctx));
  } else if (OB_FAIL(read_handles_.reserve(*access_ctx.allocator_, read_handle_cnt_))) {
    STORAGE_LOG(WARN, "failed to reserve read handles", K(ret), K_(read_handle_cnt));
  } else if (OB_FAIL(micro_handles_.reserve(*access_ctx.allocator_, micro_handle_cnt_))) {
    STORAGE_LOG(WARN, "failed to reserve micro handles", K(ret), K_(micro_handle_cnt));
  } else if (OB_FAIL(sstable_micro_infos_.reserve(*access_ctx.allocator_, micro_handle_cnt_))) {
    STORAGE_LOG(WARN, "failed to reserve sstable micro infos", K(ret), K_(micro_handle_cnt));
  } else if (OB_FAIL(sorted_sstable_micro_infos_.reserve(*access_ctx.allocator_, micro_handle_cnt_))) {
    STORAGE_LOG(WARN, "failed to reserve sorted sstable micro infos", K(ret), K_(micro_handle_cnt));
  } else {
    sstable_ = static_cast<ObSSTable*>(table);
    is_base_ = sstable_->is_major_sstable();
    iter_param_ = &iter_param;
    access_ctx_ = &access_ctx;
    query_range_ = query_range;
    sstable_snapshot_version_ = is_base_ ? 0 : sstable_->get_snapshot_version();
    table_type_ = sstable_->get_key().table_type_;
    scan_step_ = access_ctx_->query_flag_.is_reverse_scan() ? -1 : 1;
    micro_info_iter_.set_reverse(access_ctx_->query_flag_.is_reverse_scan());
    table_store_stat_.pkey_ = access_ctx_->pkey_;
    block_cache_ = &(ObStorageCacheSuite::get_instance().get_block_cache());
    if (OB_FAIL(ret)) {
    } else if (OB_ISNULL(storage_file_ = sstable_->get_storage_file_handle().get_storage_file())) {
      ret = OB_ERR_UNEXPECTED;
      STORAGE_LOG(WARN, "fail to get file handle", K(ret), K(sstable_->get_storage_file_handle()));
    } else if (OB_FAIL(prefetch())) {
      STORAGE_LOG(WARN, "Fail to prefetch data, ", K(ret));
    } else {
      is_opened_ = true;
    }
  }

  if (OB_UNLIKELY(!is_opened_)) {
    reset();
  }
  return ret;
}

int reserve(common::ObIAllocator &allocator, const int64_t count)
{
  int ret = common::OB_SUCCESS;
  if (capacity_ < count) {
    void* buf = nullptr;
    reset();
    if (OB_ISNULL(buf = allocator.alloc(sizeof(T) * count))) {
      ret = common::OB_ALLOCATE_MEMORY_FAILED;
      STORAGE_LOG(WARN, "failed to allocate array", K(ret));
    } else {
      array_ = new (buf) T[count];
      capacity_ = count;
    }
  }
  return ret;
}

int ObSSTableRowIterator::prefetch()
{
  int ret = OB_SUCCESS;
  int64_t prefetch_handle_cnt = 0;
  int64_t prefetching_handle_cnt = cur_prefetch_handle_pos_ - cur_read_handle_pos_;
  int64_t sstable_micro_cnt = 0;
  int64_t prefetch_micro_cnt = 0;
  int64_t total_sstable_micro_cnt = 0;
  int64_t prefetching_micro_cnt = cur_prefetch_micro_pos_ - cur_read_micro_pos_;
  int64_t prefetching_micro_handle_cnt = cur_fetch_handle_pos_ - cur_read_handle_pos_;

  if (!prefetch_block_end_) {
    if (!prefetch_handle_end_) {
      // prefetch read handle
      if (prefetching_handle_cnt <= read_handle_cnt_ / 2 && prefetching_handle_cnt <= prefetch_handle_depth_ / 4) {
        prefetch_handle_cnt = std::min(read_handle_cnt_ - prefetching_handle_cnt, prefetch_handle_depth_);
        prefetch_handle_depth_ = min(read_handle_cnt_, prefetch_handle_depth_ * 2);
      }
      if (OB_FAIL(prefetch_handle(prefetch_handle_cnt))) {
        STORAGE_LOG(WARN, "Fail to prefetch handle, ", K(ret), K(prefetch_handle_cnt));
      }
    }

    if (OB_SUCC(ret)) {
      if ((prefetching_micro_cnt <= micro_handle_cnt_ / 2 && prefetching_micro_cnt <= prefetch_micro_depth_ / 4) ||
          0 == prefetching_micro_handle_cnt || 0 == prefetching_micro_cnt) {
        // prefetching micro count is less than free micro count and prefetch micro depth
        prefetch_micro_cnt = std::min(micro_handle_cnt_ - prefetching_micro_cnt, prefetch_micro_depth_);
        prefetch_micro_depth_ = min(micro_handle_cnt_, prefetch_micro_depth_ * 2);
      }
    }
    STORAGE_LOG(DEBUG,
        "prefetch info",
        K(prefetching_micro_handle_cnt),
        K(prefetching_micro_cnt),
        K(prefetch_micro_depth_),
        K(prefetch_micro_cnt),
        K(prefetch_handle_depth_),
        K(prefetch_handle_cnt),
        K(read_handle_cnt_),
        K(micro_handle_cnt_));

    while (OB_SUCC(ret) && total_sstable_micro_cnt < prefetch_micro_cnt) {
      if (OB_FAIL(micro_info_iter_.get_next_micro(sstable_micro_infos_[sstable_micro_cnt]))) {
        if (OB_ITER_END == ret) {
          ret = OB_SUCCESS;
          if ((micro_handle_cnt_ >= LIMIT_PREFETCH_BLOCK_CACHE_THRESHOLD &&
                  cur_fetch_handle_pos_ > (cur_prefetch_handle_pos_ + cur_read_handle_pos_) / 2)) {
            break;
          } else if (cur_fetch_handle_pos_ >= cur_prefetch_handle_pos_) {
            if (prefetch_handle_end_) {
              prefetch_block_end_ = true;
            }
            break;
          } else if (OB_FAIL(micro_info_iter_.open(this, read_handles_[cur_fetch_handle_pos_ % read_handle_cnt_]))) {
            STORAGE_LOG(WARN,
                "Fail to open read handle, ",
                K(ret),
                K(sstable_micro_cnt),
                K_(cur_prefetch_handle_pos),
                K_(cur_fetch_handle_pos),
                K_(cur_read_handle_pos));
          } else {
            ++cur_fetch_handle_pos_;
          }
        } else {
          STORAGE_LOG(WARN, "Fail to get next sstable micro info, ", K(ret));
        }
      } else {
        sorted_sstable_micro_infos_[sstable_micro_cnt] = sstable_micro_infos_[sstable_micro_cnt];
        sstable_micro_cnt += sstable_micro_infos_[sstable_micro_cnt].is_skip_ ? 0 : 1;
        total_sstable_micro_cnt++;
      }
    }

    // prefetch micro block
    if (OB_SUCC(ret)) {
      if (sstable_micro_cnt > 0) {
        if (OB_FAIL(prefetch_block(sstable_micro_cnt))) {
          STORAGE_LOG(WARN, "Fail to prefetch block, ", K(ret));
        } else {
          STORAGE_LOG(DEBUG,
              "Success to prefetch block, ",
              K(sstable_micro_cnt),
              K_(cur_read_handle_pos),
              K_(cur_fetch_handle_pos),
              K_(cur_prefetch_handle_pos));
        }
      }
    }
  }
  return ret;
}

void ObTableScanStoreRowIterator::reuse_row_iters()
{
  if (NULL != single_merge_) {
    single_merge_->reuse();
  }
  if (NULL != get_merge_) {
    get_merge_->reuse();
  }
  if (NULL != scan_merge_) {
    scan_merge_->reuse();
  }
  if (NULL != multi_scan_merge_) {
    multi_scan_merge_->reuse();
  }
  if (NULL != index_merge_) {
    index_merge_->reuse();
  }
  if (NULL != row_sample_iterator_) {
    row_sample_iterator_->reuse();
  }
  if (NULL != block_sample_iterator_) {
    block_sample_iterator_->reuse();
  }
}

ObSSTableRowIterator::~ObSSTableRowIterator()
{
  if (NULL != micro_exister_) {
    micro_exister_->~ObMicroBlockRowExister();
    micro_exister_ = NULL;
  }
  if (NULL != micro_getter_) {
    micro_getter_->~ObMicroBlockRowGetter();
    micro_getter_ = NULL;
  }
  if (NULL != micro_scanner_) {
    micro_scanner_->~ObIMicroBlockRowScanner();
    micro_scanner_ = NULL;
  }
  if (NULL != micro_lock_checker_) {
    micro_lock_checker_->~ObMicroBlockRowLockChecker();
    micro_lock_checker_ = NULL;
  }
}
